answerCss:
main {
  position: relative;
}

.video_container {

}

.video_container img {
  width: 100%;
  vertical-align: top;
}

.overlay {
  position: absolute;
  inset: 0;
}

.overlay .play_button {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 150px;
  height: 150px;
  background-color: rgba(255, 255, 255, 0.3);
  border: none;
  border-radius: 50%;
  backdrop-filter: blur(5px);
}
