answerCss:
main {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 200px;
  height: 200px;
  background-color: white;
}

.btn {
  /* 버튼 border width는 5px, linear-gradient(red, blue) */
  position: relative;
  width: 150px;
  height: 150px;
  font-size: 30px;
  border-radius: 10px;
  border: none;
}

.btn::before {
  position: absolute;
  inset: 0;
  background-image: linear-gradient(red, blue);
  border-radius: inherit;
  content: '';
}

.btn::after {
  position: absolute;
  inset: 5px;
  background-color: white;
  line-height: 150px;
  border-radius: 5px;
  content: 'click';
}
