defaultUserCss:
main {
  position: relative;
  width: 200px;
  height: 200px;
  font-size: 0;
  background-color: white;
}

.item {
  display: inline-block;
  vertical-align: top;
  width: 100px;
  height: 100px;
  background-color: white;
  font-size: 20px;
  border: 2px solid black;
  box-sizing: border-box
}

.item:nth-child(1) {
  border-color: red;
}

.item:nth-child(2) {
  border-color: blue;
}

.item:nth-child(3) {
  border-color: green;
}

.item:nth-child(4) {
  border-color: magenta;
}

.item:nth-child(5) {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border-color: black;
}

