defaultUserCss:
.text {
  background-image: linear-gradient(45deg, #113843, #fff);
  font-size: 30px;
  font-weight: bold;
  &::before {
    content: '';
    height: 15px;
    background: #903843;
  }
}

answerCss:
.text {
  position: relative;
  background-image: linear-gradient(45deg, #113843, #fff);
  -webkit-background-clip: text;
  font-size: 30px;
  font-weight: bold;
  color: transparent;
  &::before {
    content: '';
    position: absolute;
    right: 0;
    bottom: 0;
    left: 0;
    height: 15px;
    background: #903843;
    z-index: -1;
  }
}
