defaultUserCss:
.item {
  display: inline-block;
  width: 23px;
  height: 23px;
  margin-top: 4px;
  border: 1px solid currentcolor;
  font-size: 16px;
  line-height: 25px;
  color: white;
  vertical-align: top;
  text-align: center;
  &:nth-child(5n) {
    color: green;
  }
}

answerCss:
.box {
}
.item {
  display: inline-block;
  width: 23px;
  height: 23px;
  margin-top: 4px;
  border: 1px solid currentcolor;
  font-size: 16px;
  line-height: 25px;
  color: white;
  vertical-align: top;
  text-align: center;
  &:first-child {
    color: red;
  }
  &:nth-child(2n) {
    color: orange;
  }
  &:nth-child(5n) {
    color: green;
  }
  &:nth-child(8n) {
    color: purple;
  }
  &:last-child {
    color: blue;
  }
}
