defaultUserCss:
main {
  color: #fff;
}

ul {
  padding: 5px 0;
  border: 1px solid #fff;
}

li {
  position: relative;
  list-style: none;
  padding: 0 10px;
}

li + li::before {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  width: 1px;
  background-color: grey;
  content: '';
}

answerCss:
main {
  color: #fff;
}

ul {
  display: flex;
  padding: 5px 0;
  border: 1px solid #fff;
}

li {
  position: relative;
  flex: 0 0 auto;
  list-style: none;
  padding: 0 10px;
}

li + li::before {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  width: 1px;
  background-color: grey;
  content: '';
}

li:last-of-type {
  flex: 0 1 auto;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
