defaultUserCss:
ul {
  margin: 0;
  padding: 0;
}
.item {
  list-style: none;
  padding: 10px;
  border: 1px solid white;
  color: white;
  border-radius: 10px;
  background-color: green;
  + .item {
    margin-right: 10px;
  }
  &:first-child::before {
    border: 1px solid white;
    border-radius: 50%;
    background-color: red;
    content: '';
  }
}

answerCss:
ul {
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: row-reverse;
  justify-content: left;
}
.item {
  list-style: none;
  position: relative;
  padding: 10px;
  border: 1px solid white;
  color: white;
  border-radius: 10px;
  background-color: green;
  + .item {
    margin-right: 10px;
  }
  &:first-child::before {
    position: absolute;
    top: 0;
    right: 0;
    width: 10px;
    height: 10px;
    border: 1px solid white;
    border-radius: 50%;
    background-color: red;
    content: '';
  }
}
