answerCss:
body {
  margin: 0;
}
ul {
  margin: 0;
  padding: 0;
}
.item {
  display: flex;
  align-items: center;
  list-style: none;
}
.item + .item {
  margin-top: 15px;
}
.image {
  flex: none;
  width: 150px;
  height: 100px;
  background-color: green;
}
.text {
  margin-left: 10px;
  overflow: hidden;
  color: #fff;
}
.title {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.twoline {
  display: -webkit-box;
  overflow: hidden;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  text-overflow: ellipsis;
  margin-top: 10px;
}
