body {
  margin: 0;
  background-color: #eee;
}

h2 {
  margin: 10px 0;
  text-align: center;
}

.ellipsis_one,
.ellipsis_multiple {
  width: 200px;
  margin: 10px auto 0;
  border: 10px solid white;
  background-color: white;
}

.ellipsis_one {
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}

.ellipsis_multiple {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}
