defaultUserCss:
.container {
  display: flex;
  padding: 10px;
  border-radius: 10px;
  color: #fff;
  background-color: #38393a;
}
.information {
  flex: 1;
}
.text {
  font-size: 3em;
}
.date {
  font-size: 2em;
}
.calendar {
  flex: 1;
  text-align: center;
}

th {
  font-weight: 900;
}
td {
  font-weight: 500
}
.sunday {
  color: #f33;
}

.blind {
  overflow: hidden;
  position: absolute;
  clip: rect(0 0 0 0);
  width: 1px;
  height: 1px;
  margin: -1px;
}

