.container {
  position: relative;
  height: 300px;
  background-color: #303030;
  color: #000;
}
.first {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 100;
  width: 50px;
  height: 50px;
  background-color: #585;
}
.second {
  position: absolute;
  top: 0;
  right: 0;
  z-index: 1;
  width: 300px;
  height: 100px;
  background-color: #0ff;
  text-align: right;
}
.third {
  position: absolute;
  width: 250px;
  height: 200px;
  background-color: #fff;
}
