defaultUserCss:
ol {
  margin: 0;
  padding: 0;
}
li {
  list-style: none;
  padding: 10px;
  border: 2px solid #03c75a;
  color: #000;
  border-radius: 10px;
  background-color: #fff;
  counter-increment: section;
  + li {
    margin-right: 10px;
  }
  &::before {
    content: counter(section)
  }
}

answerCss:
ol {
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: row-reverse;
  justify-content: left;
}
li {
  list-style: none;
  position: relative;
  padding: 10px;
  border: 2px solid #03c75a;
  color: #000;
  border-radius: 10px;
  background-color: #fff;
  counter-increment: section;
  + li {
    margin-right: 10px;
  }
  &::before {
    content: counter(section)
  }
}
