defaultUserCss:
.title {
  display: block;
  padding-left: 10px;
  font-size: 20px;
  color: white;
}
.pad {
  overflow: hidden;
  display: grid;
  grid-template-rows: 50px 50px;
  grid-template-columns: 50px 50px;
  gap: 1px;
  margin: 10px;
  width: 102px;
  height: 102px;
  border-radius: 6px;
  background-color: black;
}
.label {
  position: relaive;
  cursor: pointer;
}
.input {
  position: absolute;
  z-index: -1;
  opacity: 0;
}

answerCss:
.title {
  display: block;
  padding-left: 10px;
  font-size: 20px;
  color: white;
}
.pad {
  overflow: hidden;
  display: grid;
  grid-template-rows: 50px 50px;
  grid-template-columns: 50px 50px;
  gap: 1px;
  margin: 10px;
  width: 102px;
  height: 102px;
  border-radius: 6px;
  background-color: black;
}
.label {
  position: relaive;
  background-color: white;
  cursor: pointer;
}
.input {
  position: absolute;
  z-index: -1;
  opacity: 0;
}
.input:checked + .first {
  background-color: red;
}
.input:checked + .second {
  background-color: blue;
}
.input:checked + .third {
  background-color: green;
}
.input:checked + .fourth {
  background-color: yellow;
}
