defaultUserCss:
.container {
  padding: 20px;
  text-align: center;
  background-color: white;
}
label {
  display: flex;
  align-items: center;
  + label {
    margin-top: 10px;
  }
}
.icon {
  width: 30px;
  height: 30px;
  border: 1px solid gray;
  border-radius: 8px;
}
.checkbox {
  position: absolute;
  overflow: hidden;
  clip: rect(0 0 0 0);
  width: 1px;
  height: 1px;
  margin: -1px;
}
.text {
  margin-left: 10px;
}

answerCss:
.container {
  padding: 20px;
  text-align: center;
  background-color: white;
}
label {
  display: flex;
  align-items: center;
  + label {
    margin-top: 10px;
  }
}
.icon {
  width: 30px;
  height: 30px;
  border: 1px solid gray;
  border-radius: 8px;
}
.checkbox {
  position: absolute;
  overflow: hidden;
  clip: rect(0 0 0 0);
  width: 1px;
  height: 1px;
  margin: -1px;
  &:checked {
    + .icon {
      background-color: lightblue;
    }
  }
}
.text {
  margin-left: 10px;
}
