answerCss:
.container {
  padding:10px 0;
  text-align: center;
  background-color: white;
}
.button {
  width: 70px;
  height: 70px;
  &:first-child {
    border: 3px solid green;
  }
  &:nth-child(2) {
    border: 3px dashed green;
  }
  &:nth-child(3) {
    border: 3px dotted green;
  }
  &:nth-child(4) {
    border: none;
  }
  &:nth-child(5) {
    border: 3px solid green;
    border-radius: 50%;
  }
}
